      <h2>🌈도시:{props.city}</h2>
      <p>🥳시간:{props.time}시</p>
    </div>
  )
}

function App() {
  const cityTimeData = [
    ['서울',10],
    ['베이징',9],
    ['시드니',12],
  ]

  const WorldClockList = cityTimeData.map((citytime,index)=>
    <WorldClock city={citytime[0]} time={citytime[1]} key={index}/>  
  )
  return (
    <div className="App">
      {/* <h1 style={{color:'red'}}>gkdlfn</h1>  */}
      {/* <h1 style={myStyle}>gkdlfn</h1>  */}
